import React, { useState } from "react";


class UserInput extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name: "",
            votes: ""
        }
    }


    handleSubmit = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }


    checkSubmitted = (e) => {
        console.log(this.state);
        e.preventDefault();     // prevent automatically refresh the page
    }

    // adding name field in input tag so [name]: value could map them
    render() {
        return (
            <div>
                <form onSubmit={this.checkSubmitted}>
                    <label className="col-3 row-3">
                        Name:
                        <input name="name" type="text" value={this.state.name} onChange={this.handleSubmit}/> 
                    </label>
                    <br></br>
                    <label className="col-3 row-3">
                        votes:
                        <input name="votes" type="number" value={this.state.id} onChange={this.handleSubmit}/>
                    </label>
                    <button>Submit</button>
                </form>
            </div>
        );
    }
};

export default UserInput;
