import React from "react";
import ReactDOM from "react-dom";
import "./Moveable.css"

class Moveable extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            difX: 0, 
            difY: 0,
            dragging: false,
            style: {},
            objects: [],
            outputText: ""
        }

        this.dragStart = this.dragStart.bind(this);
        this.drag = this.drag.bind(this);
        this.dragEnd = this.dragEnd.bind(this);
        this.addObject = this.addObject.bind(this);
        this.deleteObject = this.deleteObject.bind(this);
    };
    

    dragStart(e) {
        this.setState({
            difX: e.screenX - e.currentTarget.getBoundingClientRect().left,
            difY: e.screenY - e.currentTarget.getBoundingClientRect().top,
            dragging: true,
        });
    };


    drag(e) {
        if (this.state.dragging)
        {
            let left = e.screenX - this.state.difX;
            let top = e.screenY - this.state.difY;
            this.setState({
                style: {
                    left: left,
                    top: top
                }
            });
        }
    };


    dragEnd() {
        this.setState({
            dragging: false
        });
    };


    addObject() {
        this.setState([
            ...this.state.objects,
            { id: this.state.objects.length() + 1, text: "default" }
        ]);
    }



    render() {
        return (
            <div>
                <button className="btn btn-outline-dark" onClick={addObject}></button>
                <div>
                    { this.state.objects.map(function(object) {
                        return <div key={object.id}>
                            <button className="btn btn-outline-dark" onClick={addObject}></button>
                            <div className="Moveable" style={ this.state.style } onMouseDown={this.dragStart} onMouseMove={this.drag} onMouseUp={this.dragEnd}>
                                <textarea>{object.text}</textarea>
                                <button onClick={editObject}>edit</button>
                                <button onClick={deleteObject}>delete</button>
                            </div>
                        </div>
                        })
                    }
                </div>
            </div>
        );
    };
};


export default Moveable;
