import React, { useState } from "react";
import Axios from 'axios';

function UserInput() {

    const [id, setId] = useState("");
    const [proposer, setProposer] = useState("");
    const [idea, setIdea] = useState("");
    const [votes, setVotes] = useState(0);

    handleSubmit = (event) => {
        this.setState({ [event.target.name]: event.target.value });
        Axios.post("http://localhost:3001/api/insert", {
            id: this.state.id,
            proposer: this.state.proposer,
            idea: this.state.proposer,
            votes: this.state.votes}
        ).then(() => {
            alert("success insert!");
        }).catch((err) => {
            console.log(err);
        })
    }


    checkSubmitted = (e) => {
        console.log(this.state);
        e.preventDefault();     // prevent automatically refresh the page
    }

    // adding name field in input tag so [name]: value could map them
    return (
        <div>
            <form onSubmit={this.checkSubmitted}>
                <label className="col-3">
                    id:
                    <input name="id" type="text" value={this.state.id} />
                </label>
                <br></br>
                <label className="col-3 row-3">
                    proposer:
                    <input name="proposer" type="text" value={this.state.name} /> 
                </label>
                <br></br>
                <label className="col-3">
                    idea:
                    <input name="idea" type="text" value={this.state.idea} />
                </label>
                <br></br>
                <label className="col-3 row-3">
                    votes:
                    <input name="votes" type="number" value={this.state.votes} />
                </label>
                <button onClick={this.handleSubmit}>Submit</button>
            </form>
        </div>
        );
    }

export default UserInput;
