import React, { useState } from 'react';
import { Form, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Axios from 'axios';

function Login() {

    //const [newId, setNewId] = useState("");
    const [nameReg, setNameReg] = useState("");
    const [passwordReg, setPasswordReg] = useState("");

    const [nameLog, setNameLog] = useState("");
    const [passwordLog, setPasswordLog] = useState("");

    const handleRegister = () => {
        Axios.post("http://localhost:3001/register", {
            name: nameReg,
            password: passwordReg
        }).then((response) => {
            console.log(response);
        });
    };

    const handleLogin = (e) => {
        Axios.get("http://localhost:3001/login", {
            name: nameLog,
            password: passwordLog
        }).then((response) => {
            if (response.data.message)
                window.alert(response.data.message);
            else
                console.log(response.data);
        });

        e.preventDefault();
    };


    return (
        <div>
            <div className="box">
                <label>Registration</label>
                <form onSubmit={handleRegister}>
                    <input placeholder='username' onChange={(e) => { setNameReg(e.target.value) }}/>
                    <br></br>
                    <input placeholder="password" onChange={(e) => { setPasswordReg(e.target.value) }}/>
                    <br></br>
                    <button className="float-right">Register</button>
                </form>
            </div>
            <br></br>
            <div className="box">
                <label>Login</label>
                <form onSubmit={(e) => handleLogin(e)}>
                    <input placeholder='your username' onChange={(e) => { setNameLog(e.target.value) }}/>
                    <br></br>
                    <input placeholder='your password' onChange={(e) => { setPasswordLog(e.target.value) }}/>
                    <br></br>
                    <button className="float-right">Login</button>
                </form>
            </div>
        </div>
    );
};

export default Login;
