import React from "react";
import ReactDOM from "react-dom";
import "./Moveable.css"

class Moveable extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            difX: 0, 
            difY: 0,
            dragging: false,
            objects: [],
            outputText: "",
            display: null
        }



        this.dragStart = this.dragStart.bind(this);
        this.drag = this.drag.bind(this);
        this.dragEnd = this.dragEnd.bind(this);
        this.addObject = this.addObject.bind(this);
        this.deleteObject = this.deleteObject.bind(this);
        this.editObject = this.editObject.bind(this);
        this.show = this.show.bind(this);
    };
    

    dragStart(e) {
        this.setState({
            difX: e.screenX - e.currentTarget.getBoundingClientRect().left,
            difY: e.screenY - e.currentTarget.getBoundingClientRect().top,
            dragging: true,
        });
    };


    drag(e) {
        if (this.state.dragging)
        {
            let left = e.screenX - this.state.difX;
            let top = e.screenY - this.state.difY;
            this.setState({
                style: {
                    left: left,
                    top: top
                }
            });
        }
    };


    dragEnd() {
        this.setState({
            dragging: false
        });
    };


    addObject() {
        this.setState({
            objects: [...this.state.objects, { id: this.state.objects.length + 1, text: "default", style: { left: this.state.difX, top: this.state.difY } }]
        });
    }


    deleteObject(e) {
        this.setState({
            objects: [...this.state.objects.filter(object => object.id != e.target.key)]
        });
    }


