    show() {
        this.setState({
            display: this.state.objects.map(function(object) {
                return <div key={object.id}>
                    <div className="Moveable" style={ object.style } onMouseDown={ (this) => this.dragStart } onMouseMove={this.drag} onMouseUp={this.dragEnd}>
                        <textarea disabled={false}>{object.text}</textarea>
                        <button key={object.id} onClick={this.editObject}>edit</button>
                        <button onClick={this.deleteObject}>delete</button>
                    </div>
                </div>
                })
            }
        )
    }
    */

    render() {
        return (
            <div>
                <button className="btn btn-outline-dark" onClick={ this.addObject}>add moveable</button>
                <div>
                    { 
                        this.state.objects.map((object) => {
                        return <div key={object.id}>
                            <div className="Moveable" style={ object.style } onMouseDown={ this.dragStart } onMouseMove={this.drag} onMouseUp={this.dragEnd}>
                                <textarea disabled={false}>{object.text}</textarea>
                                <button key={object.id} onClick={this.editObject}>edit</button>
                                <button onClick={this.deleteObject}>delete</button>
                            </div>
                        </div>
                        })
                    }
                </div>
            </div>
        );
    };
};


export default Moveable;
