import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Modal } from "reactstrap";


function DisplayIdeas() {
    const [ideaList, setIdeaList] = useState([]);

    const [newIdea, setNewIdea] = useState("");
    //const [newVote, setNewVote] = useState(0);
    //const [disableVote, setDisableVote] = useState(false);
    // set up an array to denote all the ideas user votes       bool[ideaList.length()]

    useEffect(() => {
        Axios.get("http://localhost:3001/api/get").then((response) => {
            setIdeaList(response.data);
        });
    });
    // empty array in the end means it should only run once; no array means fetch data everytime render DOM tree

    /*
    setIdeaList([
        ...ideaList,
        {}
    ]);
    */

    const updateIdea = (event) => {
        console.log(event.target.value);

        Axios.put(`http://localhost:3001/api/update/idea/${event.target.value}`, {
            idea: newIdea,
        })
    };

    const handelDelete= (event) => {
        Axios.delete(`http://localhost:3001/api/delete/${event.target.value}`);
    };


    const updateVotes = (idea) => {
        console.log(idea);
        Axios.put(`http://localhost:3001/api/update/votes/${idea.id}`, {
            votes: Number(idea.votes) + 1
        });
        //setDisableVote(true);
    };

    // float-right for align right on buttons, text-right for texts

    // TODO: update, delete
    const table = ideaList.map((idea) => {
        return (
            <div className="card" key={idea.id}>
                <button className="btn-group ml-auto" value={ idea.id } onClick={handelDelete}>delete</button>
                <p>Idea ID: {idea.id}, Proposed by {idea.proposer}</p>
                <p>{idea.idea}</p>
                <p className="text-right">votes: {idea.votes}</p>
                <div>
                    <button className="btn-group float-left" value={idea.id} >edit</button>
                    <button className="btn-group float-right" disabled={false} value={ idea.id } onClick={ () => updateVotes(idea) }>vote</button>
                </div>
            </div>
        );
    });

    return (
        <div>
            {table}
        </div>
    );
};


export default DisplayIdeas;
