import React, { useState } from "react";


class UserInput extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name: "",
            id: ""
        }
    }


    handleSubmit = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }


    checkSubmitted = (e) => {
        console.log(this.state);
        e.preventDefault();     // prevent automatically refresh the page
    }

    render() {
        return (
            <div>
                <form onSubmit={this.checkSubmitted}>
                    <label className="col-3 row-3">
                        Name:
                        <input type="text" value={this.state.name} onChange={this.handleSubmit}/> 
                    </label>
                    <br></br>
                    <label className="col-3 row-3">
                        ID:
                        <input type="text" value={this.state.id} onChange={this.handleSubmit}/>
                    </label>
                    <br></br>
                    <button className="col-2">Submit</button>
                </form>
            </div>
        );
    }
};

export default UserInput;
