import React, { useState } from 'react';
import { Form, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Axios from 'axios';

function Login() {

    //const [newId, setNewId] = useState("");
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");

    const handleRegister = () => {
        Axios.post("http://localhost:3001/api/addUser", {
            name: name,
            password: password
        }).then((response) => {
            console.log(response);
        });
    };

    const handleLogin = () => {
        Axios.get("http://localhost:3001/api/login", {
            name: name,
            password: password
        }).then((response) => {
            console.log(response);
        });
    };


    return (
        <div>
            <div>
                <form onSubmit={handleRegister}>
                    <label className="col-3">User Name:</label>
                        <input onChange={(e) => { setName(e.target.value) }}/>
                    <br></br>
                    <label className="col-3">Password:</label>
                        <input onChange={(e) => { setPassword(e.target.value) }}/>
                    <br></br>
                    <button>Register</button>
                </form>
            </div>
            <div>
                <form>
                    <label className="col-2">User Name:</label>
                        <input onChange={(e) => { setName(e.target.value) }}/>
                    <br></br>
                    <label className="col-2">Password:</label>
                        <input onChange={(e) => { setPassword(e.target.value) }}/>
                    <br></br>
                    <button>Login</button>
                </form>
            </div>
        </div>
    );
};

export default Login;
