import React from "react";
import ReactDOM from "react-dom";
import "./Moveable.css"

class Moveable extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            difX: 0, 
            difY: 0,
            dragging: false,
            style: {}
        }

        this.dragStart = this.dragStart.bind(this);
        this.drag = this.drag.bind(this);
        this.dragEnd = this.dragEnd.bind(this);
    };
    
    dragStart(e) {
        this.setState({
            difX: e.screenX - e.currentTarget.getBoundingClientRect().left,
            difY: e.screenY - e.currentTarget.getBoundingClientRect().top,
            dragging: true,
        });
    };


    drag(e) {
        if (this.state.dragging)
        {
            let left = e.screenX - this.state.difX;
            let top = e.screenY - this.state.difY;
            this.setState({
                style: {
                    left: left,
                    top: top
                }
            });
        }
    };


    dragEnd() {
        this.setState({
            dragging: false
        });
    };



    render() {
        return (
            <div className="Moveable" style={ this.state.style } onMouseDown={this.dragStart} onMouseMove={this.drag} onMouseUp={this.dragEnd}>
                this is a Moveable component
            </div>
        );
    };
};


export default Moveable;
