import React, { useState } from "react";
import Axios from 'axios';

class UserInput extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id: "",
            proposer: "",
            idea: "",
            votes: 0
        }
    }


    handleSubmit = (event) => {
        this.setState({ [event.target.name]: event.target.value });
        Axios.post("/api/insert", {id: this.state.id, proposer: this.state.proposer, idea: this.state.proposer, votes: this.state.votes});
    }


    checkSubmitted = (e) => {
        console.log(this.state);
        e.preventDefault();     // prevent automatically refresh the page
    }

    // adding name field in input tag so [name]: value could map them
    render() {
        return (
            <div>
                <form onSubmit={this.checkSubmitted}>
                    <label className="col-3">
                        id:
                        <input name="id" type="text" value={this.state.id} onChange={this.handleSubmit}/>
                    </label>
                    <br></br>
                    <label className="col-3 row-3">
                        proposer:
                        <input name="proposer" type="text" value={this.state.name} onChange={this.handleSubmit}/> 
                    </label>
                    <br></br>
                    <label className="col-3">
                        idea:
                        <input name="idea" type="text" value={this.state.idea} onChange={this.handleSubmit}/>
                    </label>
                    <br></br>
                    <label className="col-3 row-3">
                        votes:
                        <input name="votes" type="number" value={this.state.votes} onChange={this.handleSubmit}/>
                    </label>
                    <button>Submit</button>
                </form>
            </div>
        );
    }
};

export default UserInput;
