import React from "react";
import ReactDOM from "react-dom";
import "./Moveable.css"

class Moveable extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            difX: 0, 
            difY: 0,
            dragging: false,
            style: {}
        }

        this.dragStart = this.dragStart.bind(this);
        this.drag = this.drag.bind(this);
        this.dragEnd = this.dragEnd.bind(this);
    };
    
    dragStart(e) {
        this.setState({
            difX: e.screenX - e.currentTarget.getBoundingClientRect().left,
            difY: e.screeY - e.currentTarget.getBoundingClientRect().top,
            dragging: true,
        });
    };


    drag(e) {
        let left = e.screenX - this.state.difX;
        let right = e.screeY - this.state.difY;

        if (this.state.dragging)
        {
            this.setState({
                style: {
                    left: left,
                    right: right
                }
            });
        }
    };


    dragEnd() {
        this.setState({
            dragging: false
        });
    };



    render() {
        return (
            <div style={ this.state.style } onMouseDown={this.dragStart} onMouseMove={this.drag} onMouseUp={this.dragEnd}>
                <svg>
                </svg>
                <p>this is a Moveable component</p>
            </div>
        );
    };
};


export default Moveable;
